import { ReactComponent as UkraineMap } from './ukraine.svg';

const Map = (props) => {

  const [selected, setSelected] = useState(null);

  const handlerSelectRegion = () => {
    props.handler()
  }

  useEffect(() => {
    const regions = document.getElementById('map').childNodes;

    for(let i = 0; i < regions.length; i++) {
      regions[i].addEventListener( 'click', () => {
        setSelected(regions[i].attributes.id.nodeValue)
      })
    }

  })

  // const regions = document.getElementById('map').childNodes;

  return (
    <div className="map">
      <UkraineMap />
    </div>
  )
}

export default Map;
