import React, { useState } from 'react';
import './App.css';
import { Map } from './components/Map';
import { Bar } from './components/Bar';

const current = {
  "icon": "mostly_cloudy",
  "icon_num": 5,
  "summary": "Mostly cloudy",
  "temperature": 13,
  "wind": {
      "speed": 5.4,
      "angle": 225,
      "dir": "SW"
  },
  "precipitation": {
      "total": 0,
      "type": "none"
  },
  "cloud_cover": 74
}

const App = () => {
  // eslint-disable-next-line
  const [cache, setCache] = useState({ current: current });
  const [region, setRegion] = useState( null );

  const handlerSetRegion = (el) => setRegion( el );

  return (
    <div className='app'>
      <Map handler={ handlerSetRegion}  selected={ region } />
      <Bar selected={ region } data={ cache } />
    </div>
  )
}

export default App;
