import './index.css';

const Map = (props) => {

  const handlerSelectRegion = (region) => {
    region.firstChild.style.fill = 'black';
    props.handler(region)
  }

  useEffect(() => {
    const regions = document.getElementById('map').childNodes;
    let selected = null;

    if (props.selected != null) {
      selected = props.selected;
      props.selected.firstChild.style.fill = 'black';
    }

    for ( let i = 0; i < regions.length; i++ ) {
      regions[i].addEventListener( 'click', () => {
        handlerSelectRegion(regions[i]);
        selected = regions[i];

        for ( let l = 0; l < regions.length; l++ ) {
          regions[l].firstChild.style.fill = '#202020'
          if ( selected != null )
            selected.firstChild.style.fill = 'black';
        }

      })

      regions[i].addEventListener( 'mouseover', () => {

        for ( let l = 0; l < regions.length; l++ ) {
          regions[l].firstChild.style.fill = '#202020'
          if ( selected != null )
            selected.firstChild.style.fill = 'black';
        }

        regions[i].firstChild.style.fill = '#ff0';
      })

      regions[i].addEventListener( 'mouseout', () => {
        regions[i].firstChild.style.fill = '#202020';

        for ( let l = 0; l < regions.length; l++ ) {
          regions[l].firstChild.style.fill = '#202020'
          if ( selected != null )
            selected.firstChild.style.fill = 'black';
        }

      })
    }

  })

  return (
    <div className="map">
      <UkraineMap />
    </div>
  )
}

export { Map };
