import React, { useEffect } from 'react';
import { ReactComponent as UkraineMap } from './ukraine.svg';
import './index.css';

const Map = (props) => {

  const handlerSelectRegion = (region) => {
    props.handler( region );
  }

  const handlerClick = (item) => handlerSelectRegion( item );

  const handlerOut = (item, selected) => {
    if (item !== selected)
      item.firstChild.style.fill = "#202020";
  }

  useEffect(() => {
    document.getElementById('map').childNodes.forEach(item => {
      item.onclick = () => handlerClick( item );
      item.onmouseout = () => handlerOut( item, props.selected );

      if (props.selected != null)
        if (item === props.selected)
          item.firstChild.style.fill = "blue";
        else
          item.firstChild.style.fill = "#202020"
    })
  })

  return (
    <div className="map">
      <UkraineMap />
    </div>
  )
}

export { Map };
