import React, { useState } from 'react';
import LinearProgress from '@mui/material/LinearProgress';

import Menu from './Menu.js';
import InfoBase from './InfoBase.js';

const style = {
  display: 'flex',
  flexDirection: 'row',
  justifyContent: 'start',
  width: '100%',
  height: '25vh',
  background: '#0009'
}

const Bar = (props) => {

  const [menu, setMenu] = useState( '#base' );

  return (
    <div style={ style }>
      <Menu selected={ menu } handler={ setMenu } />
      {menu === '#base' ? (
        <InfoBase selected={ props.selected } />
      ) : menu === '#hourly' ? (
        <div></div>
      ) : menu === '#daily' ? (
        <div></div>
      ) : (
        <div></div>
      )}
    </div>
  )
}

export default Bar
