import React, { useState } from 'react';
import Stack from '@mui/material/Stack';

import Menu from './Menu.js';
import { Base } from './Board';
import { LoadedInfoCard, NotLoadedInfoCard } from './Card';

const style = {
  display: 'flex',
  flexDirection: 'row',
  justifyContent: 'start',
  width: '100%',
  height: '25vh',
  background: '#0009'
}

const Bar = (props) => {

  const [menu, setMenu] = useState( '#base' );

  const prepareBaseData = () => {
    if (Object.keys(props.data).length !== 0) {
      return {
        base: props.data.current,
          cards: {
            temperature: props.data.current.temperature,
            wind: props.data.current.wind,
            precipitation: props.data.current.precipitation,
            cloud_cover: props.data.current.cloud_cover
          }
        } 
    }
  }

  const prepareHourlyData = () => {
    if (Object.keys(props.data).length !== 0) {
      return {
        base: props.data.hourly.data[0],
          cards: props.data.hourly.data
        } 
    }
  }

  // eslint-disable-next-line
  const prepareDaysData = () => {

  }

  const data = menu === '#base' ? 
    prepareBaseData() : menu === '#hourly' ? 
    prepareHourlyData() : 
    {};

  return (
    <div style={ style }>
      <Menu selected={ menu } handler={ setMenu } />
      <Stack
        spacing={1}
        sx={{
          margin: 'auto',
          minWidth: '240px',
          justifyContent: 'start'
        }}
      >
        {props.selected !== null && Object.keys(props.data).length !== 0  ? (
          <LoadedInfoCard selected={props.selected} data={ data } />
        ) : (
          <NotLoadedInfoCard />
        )}
      </Stack>
      {menu === '#base' ? (
        <Base data={ data } />) : menu === '#hourly' ? (
          <div></div>) : menu === '#daily' ? (
          <div></div>) : (
          <div></div>
        )}
    </div>
  )
}

export default Bar
