import React from 'react';
import Box from '@mui/material/Box';
import Container from '@mui/material/Container';
import Skeleton from '@mui/material/Skeleton';
import Typography from '@mui/material/Typography';

import Card from './Card.js';

const style = {
  display: 'flex',
  flexDirection: 'column',
  justifyContent: 'space-evenly',
  width: '100%',
  height: '35vh',
  background: '#0009'
}

const city = {
  Herson: 'Херсонская область'
}

const Bar = (props) => {
  return (
    <Container style={style}>
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'center'
        }}
      >
        {(props.selected === null ? (
          <Skeleton variant="rounded" width={300} height={60} />
        ) : (
          <Typography color="light" variant="h5" display="block">
            {city[props.selected.attributes.id.nodeValue]}
          </Typography>
        ))}
      </Box>
      <Box 
        sx={{
          display: 'flex',
          justifyContent: 'center',
          flexWrap: 'wrap',
          '& > :not(style)': {
            m: 1,
            width: 128,
            height: 128,
          }
        }}
      >
        {(props.data === undefined ? Array.from(new Array(7)) : props.data).map((item, key) => (
          <Box key={key} >
            {item !== undefined ? (
              <Card />
            ) : (
              <Skeleton variant="rectangular" width={128} height={128} />
            )}
          </Box>
        ))}
      </Box>
    </Container>
  )
}

export default Bar
