import React, { useState } from 'react';
import Box from '@mui/material/Box';

import Menu from './Menu.js';
import InfoBase from './InfoBase.js';

const style = {
  display: 'flex',
  flexDirection: 'row',
  justifyContent: 'start',
  width: '100%',
  height: '25vh',
  background: '#0009'
}

const Bar = (props) => {

  const [menu, setMenu] = useState( '#base' );

  const prepareData = () => {
    if (Object.keys(props.data).length !== 0) {
      if (menu === '#base') {
        return {
          base: props.data.current,
            cards: {
              temperature: props.data.current.temperature,
              wind: props.data.current.wind,
              precipitation: props.data.current.precipitation
            }
          } 
      }
    } else {
      return {}
    }
  }

  return (
    <div style={ style }>
      <Menu selected={ menu } handler={ setMenu } />
      {props.data != null 
        ? (
          <Box>
            {menu === '#base' ? (
              <InfoBase
                selected={ props.selected }
                data={ prepareData() }
              />) : menu === '#hourly' ? (
                <div></div>
              ) : menu === '#daily' ? (
                <div></div>
              ) : (
                <div></div>
            )}
          </Box>)
        : (
          <Box>
          </Box>
        )}
    </div>
  )
}

export default Bar
