import React, { useState } from 'react';

import Menu from './Menu.js';
import InfoBase from './InfoBase.js';

const style = {
  display: 'flex',
  flexDirection: 'row',
  justifyContent: 'start',
  width: '100%',
  height: '25vh',
  background: '#0009'
}

const Bar = (props) => {

  const [menu, setMenu] = useState( '#base' );

  return (
    <div style={ style }>
      <Menu selected={ menu } handler={ setMenu } />
      {menu === '#base' ? (
        <InfoBase
          selected={ props.selected }
          menu={ menu }
          data={ Object.keys(props.data).length !== 0 ? {
            base: props.data.current,
            cards: {
              temperature: props.data.current.temperature,
              wind: props.data.current.wind,
              precipitation: props.data.current.precipitation
            }
          } : {} }
        />
      ) : menu === '#hourly' ? (
        <div></div>
      ) : menu === '#daily' ? (
        <div></div>
      ) : (
        <div></div>
      )}
    </div>
  )
}

export default Bar
