import React from 'react';
import Box from '@mui/material/Box';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemText from '@mui/material/ListItemText'
import { Typography, Skeleton } from '@mui/material';

import Divider from '@mui/material/Divider';
import Paper from '@mui/material/Paper';
import Stack from '@mui/material/Stack';

const city = {
  Ujgorod: 'Ужгород',
  Lviv: 'Львов',
  IvanFr: 'Ивано-франковск',
  Chernivci: 'Чернивци',
  Lutsk: 'Лутск',
  Ternop: 'Тернопиль',
  Rivne: 'Ривне',
  Hmel: 'Хмельницкий',
  Jitom: 'Житомыр',
  Vinn: 'Виница',
  Kiiv: 'Киев',
  Chernigiv: 'Чернигев',
  Sumy: 'Сумы',
  Cherkasy: 'Черкасы',
  Poltava: 'Полтава',
  Harkiv: 'Харькив',
  Lugansk: 'Луганск',
  Odesa: 'Одеса',
  Kirov: 'Киров',
  Dnepr: 'Днепр',
  Donetsk: 'Донецк',
  Zapor: 'Запорожье',
  Mykolaiv: 'Миколайв',
  Simfer: 'Симферопль',
  Herson: 'Херсонская область'
}

const InfoBase = (props) => {
  return (
    <Box
      sx={{
        width: '100%',
        display: 'flex',
        flexDirection: 'row',
        justifyContent: 'start'
      }}
    >
      <List
        sx={{
          marginLeft: '2.5em!important',
          minWidth: '240px'
        }}
      >
        <ListItem>
          <ListItemText>
            {props.selected !== null ? (
              <Typography variant='h4' color='whitesmoke'>
                { city[props.selected.attributes.id.nodeValue] }
              </Typography>
            ) : (
              <Box>
                <Skeleton variant="text" sx={{ fontSize: '2rem' }} />
                <Skeleton variant="text" sx={{ marginTop: '0.05em', fontSize: '4rem' }} />
              </Box>
            )}
          </ListItemText>
        </ListItem>
      </List>
      <Box
        sx={{
          width: '100%',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center'
        }}
      >
        <Stack
          direction="row"
          divider={<Divider orientation="vertical" flexItem />}
          spacing={2}
          sx={{
            justifyContent: 'center'
          }}
        >
          {props.select !== null && props.data !== undefined ? (
            <Box>
              {props.data.map((value, key) => (
                <Paper key={key}>Item</Paper>
              ))}
            </Box>
          ) : Array.from(new Array(3)).map((value, key) => (
            <Skeleton key={key} variant="rounded" width={160} height={100} />
          ))}
        </Stack>
      </Box>
    </Box>
  )
}

export default InfoBase;


