import React, { useState } from 'react';
import Box from '@mui/material/Box';
import { Typography, Skeleton } from '@mui/material';
import Stack from '@mui/material/Stack';

import { City } from '../../../constants';
import Menu from './Menu.js';
import { Base } from './Board';

const style = {
  display: 'flex',
  flexDirection: 'row',
  justifyContent: 'start',
  width: '100%',
  height: '25vh',
  background: '#0009'
}

const Bar = (props) => {

  const [menu, setMenu] = useState( '#base' );

  const prepareBaseData = () => {
    if (Object.keys(props.data).length !== 0) {
      return {
        base: props.data.current,
          cards: {
            temperature: props.data.current.temperature,
            wind: props.data.current.wind,
            precipitation: props.data.current.precipitation,
            cloud_cover: props.data.current.cloud_cover
          }
        } 
    } else {
      return {}
    }
  }

  return (
    <div style={ style }>
      <Menu selected={ menu } handler={ setMenu } />
      <Stack
        spacing={1}
        sx={{
          margin: 'auto',
          minWidth: '240px',
          justifyContent: 'start'
        }}
      >
        {props.selected !== null && Object.keys(props.data).length !== 0  ? (
          <Box
            sx={{
              display: 'flex',
              flexDirection: 'column',
              justifyContent: 'space-between',
              textAlign: 'center'
            }}
          >
            <Typography variant='h5' mt={-4} color='whitesmoke'>
              { City[props.selected.attributes.id.nodeValue] } region
            </Typography>
            <Typography variant='p' mt={2} color='whitesmoke'>
              {props.data.base.summary}  
            </Typography>
          </Box>
        ) : (
          <Box>
            <Skeleton variant="text" sx={{ fontSize: '3rem' }} />
            <Skeleton variant="text" sx={{ marginTop: '0em', fontSize: '5rem' }} />
          </Box>
        )}
      </Stack>
      {menu === '#base' ? (
        <Base
          selected={ props.selected }
          data={ prepareBaseData() }
        />) : menu === '#hourly' ? (
          <div></div>) : menu === '#daily' ? (
          <div></div>) : (
          <div></div>
        )}
    </div>
  )
}

export default Bar
