import React, { useState } from 'react';
import './App.css';
import { Map } from './components/Map';
import { Bar } from './components/Bar';
import { Url, ApiKey } from './constants.js';

const App = () => {
  // eslint-disable-next-line
  const [cache, setCache] = useState({});
  const [region, setRegion] = useState( null );

  console.log(cache)

  const handlerSetRegion = (el) => {
    setRegion( el );
  }
  // eslint-disable-next-line
  const handlerUploadApu = () => {
    const id = region.attributes.id.nodeValue;
    fetch(`${Url}/point?place_id=${id}&key=${ApiKey}`)
      .then(res => res.json())
      .then(res => setCache(Object.assign(cache, {[id]: res})))
      .catch(err => console.log(err))
  }

  if (region !== null)
    handlerUploadApu();

  return (
    <div className='app'>
      <Map handler={ handlerSetRegion}  selected={ region } />
      <Bar selected={ region } data={ cache } />
    </div>
  )
}

export default App;
