import React from 'react';
import Box from '@mui/material/Box';
import { Typography, Skeleton } from '@mui/material';
import Stack from '@mui/material/Stack';

import { City } from '../../../constants';
import { LoadedWeatherCard, NotLoadedWeatherCard } from '../Card';

const Base = (props) => {
  return (
    <Box
      sx={{
        width: '100%',
        display: 'flex',
        flexDirection: 'row'
      }}
    >
      <Stack
        spacing={1}
        sx={{
          margin: 'auto',
          minWidth: '240px',
          justifyContent: 'start'
        }}
      >
        {props.selected !== null && Object.keys(props.data).length !== 0  ? (
          <Box
            sx={{
              display: 'flex',
              flexDirection: 'column',
              justifyContent: 'space-between',
              textAlign: 'center'
            }}
          >
            <Typography variant='h5' mt={-4} color='whitesmoke'>
              { City[props.selected.attributes.id.nodeValue] } region
            </Typography>
            <Typography variant='p' mt={2} color='whitesmoke'>
              {props.data.base.summary}  
            </Typography>
          </Box>
        ) : (
          <Box>
            <Skeleton variant="text" sx={{ fontSize: '3rem' }} />
            <Skeleton variant="text" sx={{ marginTop: '0em', fontSize: '5rem' }} />
          </Box>
        )}
      </Stack>
      <Box
        sx={{
          width: '100%',
          display: 'flex',
          flexDirection: 'row',
          justifyContent: 'space-around'
        }}
      >
        {props.selected !== null && Object.keys(props.data).length !== 0  ? (
          Object.keys(props.data.cards).map((item, key) => (
            <LoadedWeatherCard key={key} title={item} data={props.data.cards[item]} />
          ))
        ) : Array.from(new Array(3)).map((value, key) => (
          <NotLoadedWeatherCard key={key} />
        ))}
      </Box>
    </Box>
  )
}

export default Base;
