import React, { useState } from 'react';
import Box from '@mui/material/Box';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemText from '@mui/material/ListItemText'
import Skeleton from '@mui/material/Skeleton';
import Typography from '@mui/material/Typography';

import Menu from './Menu.js';
// import Card from './Card.js';

const style = {
  display: 'flex',
  flexDirection: 'row',
  justifyContent: 'start',
  width: '100%',
  height: '25vh',
  background: '#0009'
}

const city = {
  Ujgorod: 'Ужгород',
  Lviv: 'Львов',
  IvanFr: 'Ивано-франковск',
  Chernivci: 'Чернивци',
  Lutsk: 'Лутск',
  Ternop: 'Тернопиль',
  Rivne: 'Ривне',
  Hmel: 'Хмельницкий',
  Jitom: 'Житомыр',
  Vinn: 'Виница',
  Kiiv: 'Киев',
  Chernigiv: 'Чернигев',
  Sumy: 'Сумы',
  Cherkasy: 'Черкасы',
  Poltava: 'Полтава',
  Harkiv: 'Харькив',
  Lugansk: 'Луганск',
  Odesa: 'Одеса',
  Kirov: 'Киров',
  Dnepr: 'Днепр',
  Donetsk: 'Донецк',
  Zapor: 'Запорожье',
  Mykolaiv: 'Миколайв',
  Simfer: 'Симферопль',
  Herson: 'Херсонская область'
}

const Bar = (props) => {

  const [menu, setMenu] = useState('#base');

  return (
    <div style={style}>
      <Menu selected={menu} handler={setMenu} />
      <Box
        sx={{
          margin: '1em',
          minWidth: '240px'
        }}
      >
        <List>
          <ListItem>
            <ListItemText>
              {props.selected !== null ? (
                <Typography variant='h3'>
                  {city[props.selected]}
                </Typography>
              ) : (
                <Skeleton variant="text" sx={{ fontSize: '3rem' }} />
              )}
            </ListItemText>
          </ListItem>
        </List>
      </Box>
    </div>
  )
}

export default Bar

