import React from 'react';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemText from '@mui/material/ListItemText'

const menuItems = [
  {
    text: 'Base information',
    code: '#base'
  },
  {
    text: 'Hourly information',
    code: '#hourly'
  },
  {
    text: 'Daily information',
    code: '#daily'
  }
];

const Menu = (props) => {

  const handlerChangeSection = (code) => props.handler( code );

  return (
    <List
      sx={{
        textAlign: 'center',
        margin: 'auto',
        marginLeft: '1.5em',
        marginRight: 0,
        color: 'whitesmoke'
      }}
    >
      {menuItems.map((item, key) => (
        <ListItem
          key={key}
          sx={{ width: 'max-content', padding: 0, margin: 'auto' }}
        >
          <ListItemButton
            selected={ item.code === props.selected }
            onClick={ () => handlerChangeSection( item.code ) }
          >
            <ListItemText
              primary={ item.text } 
              primaryTypographyProps={{
                fontSize: item.code === props.selected ? '1.25rem!important' : '0.75rem!important'
              }}
              sx={{ textAlign: 'center' }}
            />
          </ListItemButton>
        </ListItem>
      ))}
    </List>
  )

}

export default Menu;
