import React, { useState } from 'react';
import Box from '@mui/material/Box';

import Menu from './Menu.js';
import InfoBase from './InfoBase.js';
// import Card from './Card.js';

const style = {
  display: 'flex',
  flexDirection: 'row',
  justifyContent: 'start',
  width: '100%',
  height: '25vh',
  background: '#0009'
}

const Bar = (props) => {

  const [menu, setMenu] = useState( '#base' );

  return (
    <div style={ style }>
      <Menu selected={ menu } handler={ setMenu } />
      <Box
        sx={{
          display: 'flex',
          flexDirection: 'row',
          justifyContent: 'space-evenly'
        }}
      >
        {menu === '#base' ? (
          <InfoBase selected={ props.selected } />
        ) : menu === '#hourly' ? (
          <div></div>
        ) : menu === '#daily' ? (
          <div></div>
        ) : (
          <div></div>
        )}
      </Box>
    </div>
  )
}

export default Bar
