import React, { useState, useEffect } from 'react';
import { ReactComponent as UkraineMap } from './ukraine.svg';
import './index.css';

const Map = (props) => {

  const [selected, setSelected] = useState(null);

  const handlerSelectRegion = (region) => {
    props.handler(region)
  }

  const handlerClick = (evt) => {
    handlerSelectRegion(evt.targer)
    setSelected(evt.target);
  }

  useEffect(() => {
    document.getElementById('map').childNodes.forEach(item => {
      item.addEventListener( 'click', handlerClick)
      item.addEventListener( 'mouseover', function() {
        document.getElementById('map').childNodes.forEach(inItem => {
          if (inItem === item)
            inItem.firstChild.style.fill = 'yellow';
        })
      })
      item.addEventListener( 'mouseout', function(){
        document.getElementById('map').childNodes.forEach(outItem => {
          if (outItem === item && outItem !== selected)
            outItem.firstChild.style.fill = '#202020';
        })
      })
    })
  })

  return (
    <div className="map">
      <UkraineMap />
    </div>
  )
}

export { Map };
