import React from 'react';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemIcon from '@mui/material/ListItemIcon';
import ListItemText from '@mui/material/ListItemText'
import Button from '@mui/material/Button';

const menuItems = [
  {
    text: 'Текущая информация',
    code: '#base'
  },
  {
    text: 'Дневная информация',
    code: '#daily'
  },
  {
    text: 'Недельная информация',
    code: '#hourly'
  }
];

const Menu = (props) => {

  const handlerChangeSection = (code) => props.handler(code);

  return (
    <List
      sx={{
        margin: 'auto',
        marginLeft: '0',
        color: 'whitesmoke'
      }}
    >
      {menuItems.map((item, key) => (
        <ListItem
          key={key}
          sx={{ padding: 0 }}
        >
          <Button
            color={props.selected === item.code ? "secondary" : "inherit"}
            variant={props.selected === item.code ? "contained" : "outlined"}
            sx={{ padding: 0, margin: '.25em' }}
            onClick={() => handlerChangeSection(item.code)}
          >
            <ListItemButton>
              <ListItemIcon>
              </ListItemIcon>
              <ListItemText
                primary={item.text} 
              />
            </ListItemButton>
          </Button>
        </ListItem>
      ))}
    </List>
  )

}

export default Menu;
