import React, { useEffect } from 'react';
import { ReactComponent as UkraineMap } from './ukraine.svg';
import './index.css';

const Map = (props) => {

  const handlerSelectRegion = (region) => {
    props.handler(region)
  }

  const handlerClick = (item) => handlerSelectRegion(item);

  const handlerOver = (item, selected) => {
    console.log(selected)
    if (item !== selected)
      item.firstChild.style.fill = "yellow";
  }

  const handlerOut = (item, selected) => {
    if (item !== selected)
      item.firstChild.style.fill = "#202020";
  }

  useEffect(() => {
    document.getElementById('map').childNodes.forEach(item => {
      if (typeof item.onclick != "function")
        item.onclick = () => handlerClick(item);
      if (typeof item.onmouseover != "function")
        item.onmouseover = () => handlerOver(item, props.selected);
      if (typeof item.onmouseout != "function")
        item.onmouseout = () => handlerOut(item, props.selected);
    })
  })

  return (
    <div className="map">
      <UkraineMap />
    </div>
  )
}

export { Map };
