import React, { useState, useEffect } from 'react';
import { ReactComponent as UkraineMap } from './ukraine.svg';
import './index.css';

const Map = (props) => {

  const [region, setRegion] = useState(null);
  const [isLoad, setIsLoad] = useState(false);
  const [isListener, setListener] = useState(false);

  const handlerSelectRegion = (region) => {
    props.handler(region)
  }

  const setSelected = (region) => {
    setRegion(region);
  }

  const updateSelected = () => {
    if (region != null)
      region.firstChild.style.fill = 'black';
  }

  // eslint-disable-next-line
  useEffect(() => {

    window.onload = () => {
      setIsLoad(true);
    }

    if (isListener === false && isLoad === true) {
      document.getElementById('map').childNodes.forEach(item => {
        item.addEventListener( 'click', function() {
          console.log(region, 'click');
        })
        item.addEventListener( 'mouseover', function() {
          console.log(region, 'in');
        })
        item.addEventListener( 'mouseout', function(){
          console.log(region, 'out');
        })
      });

      setListener(true);
    }

  })

  return (
    <div className="map">
      <UkraineMap />
    </div>
  )
}

export { Map };
