import React from 'react';
import Box from '@mui/material/Box';
import { Typography, Skeleton } from '@mui/material';
// import Chip from '@mui/material/Chip';
import Divider from '@mui/material/Divider';
import Paper from '@mui/material/Paper';
import Stack from '@mui/material/Stack';

const city = {
  Ujgorod: 'Ужгород',
  Lviv: 'Львов',
  IvanFr: 'Ивано-франковск',
  Chernivci: 'Чернивци',
  Lutsk: 'Лутск',
  Ternop: 'Тернопиль',
  Rivne: 'Ривне',
  Hmel: 'Хмельницкий',
  Jitom: 'Житомыр',
  Vinn: 'Виница',
  Kiiv: 'Киев',
  Chernigiv: 'Чернигев',
  Sumy: 'Сумы',
  Cherkasy: 'Черкасы',
  Poltava: 'Полтава',
  Harkiv: 'Харькив',
  Lugansk: 'Луганск',
  Odesa: 'Одеса',
  Kirov: 'Киров',
  Dnepr: 'Днепр',
  Donetsk: 'Донецк',
  Zapor: 'Запорожье',
  Mykolaiv: 'Миколайв',
  Simfer: 'Симферопль',
  Herson: 'Херсонская область'
}

const InfoBase = (props) => {
  console.log(props.data)
  return (
    <Box
      sx={{
        width: '100%',
        display: 'flex',
        flexDirection: 'row'
      }}
    >
      <Stack
        spacing={1}
        sx={{
          margin: 'auto',
          marginLeft: '2.5em',
          minWidth: '240px',
          justifyContent: 'start'
        }}
      >
        {props.selected !== null ? (
          <Box
            sx={{
              display: 'flex',
              flexDirection: 'column',
              justifyContent: 'space-between',
              textAlign: 'center'
            }}
          >
            <Typography variant='h4' mt={-4} color='whitesmoke'>
              { city[props.selected.attributes.id.nodeValue] }
            </Typography>
            <Typography variant='p' mt={2} color='whitesmoke'>
              {props.data.base.summary}  
            </Typography>
          </Box>
        ) : (
          <Box>
            <Skeleton variant="text" sx={{ fontSize: '3rem' }} />
            <Skeleton variant="text" sx={{ marginTop: '0em', fontSize: '5rem' }} />
          </Box>
        )}
      </Stack>
      <Box
        sx={{
          width: '100%',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center'
        }}
      >
        <Stack
          direction="row"
          divider={<Divider orientation="vertical" flexItem />}
          spacing={2}
          sx={{
            justifyContent: 'center'
          }}
        >
          {props.selected !== null && Object.keys(props.data.cards).length !== 0  ? (
            props.data.cards.map((value, key) => (
              <Paper key={key}>
                <Stack>
                </Stack>
              </Paper>
            ))
          ) : Array.from(new Array(3)).map((value, key) => (
            <Skeleton key={key} variant="rounded" width={160} height={100} />
          ))}
        </Stack>
      </Box>
    </Box>
  )
}

export default InfoBase;
