import React, { useState } from 'react';
import './App.css';
import { Map } from './components/Map';
import { Bar } from './components/Bar';

const App = () => {

  const [region, setRegion] = useState(null);

  const handlerSetRegion = (el) => {
    if (region !== el) {
      setRegion(el);
    }
  }

  return (
    <div className='app'>
      <Bar selected={region} />
      <Map handler={handlerSetRegion} selected={region} />
    </div>
  )
}

export default App;
