import React, { useEffect } from 'react';
import { ReactComponent as UkraineMap } from './ukraine.svg';

const Map = (props) => {

  const handlerSelectRegion = (region) => {
    props.handler(region)
  }

  useEffect(() => {
    const regions = document.getElementById('map').childNodes;

    for(let i = 0; i < regions.length; i++) {
      regions[i].addEventListener( 'click', () => {

        handlerSelectRegion(regions[i].attributes.id.nodeValue);
        regions[i].style.fill = 'black';

      })
    }

  })

  // const regions = document.getElementById('map').childNodes;

  return (
    <div className="map">
      <UkraineMap />
    </div>
  )
}

export default Map;
