import React from 'react';
import Box from '@mui/material/Box';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemText from '@mui/material/ListItemText'
import { Typography, Skeleton } from '@mui/material';
import { Button, ButtonGroup } from '@mui/material';

const city = {
  Ujgorod: 'Ужгород',
  Lviv: 'Львов',
  IvanFr: 'Ивано-франковск',
  Chernivci: 'Чернивци',
  Lutsk: 'Лутск',
  Ternop: 'Тернопиль',
  Rivne: 'Ривне',
  Hmel: 'Хмельницкий',
  Jitom: 'Житомыр',
  Vinn: 'Виница',
  Kiiv: 'Киев',
  Chernigiv: 'Чернигев',
  Sumy: 'Сумы',
  Cherkasy: 'Черкасы',
  Poltava: 'Полтава',
  Harkiv: 'Харькив',
  Lugansk: 'Луганск',
  Odesa: 'Одеса',
  Kirov: 'Киров',
  Dnepr: 'Днепр',
  Donetsk: 'Донецк',
  Zapor: 'Запорожье',
  Mykolaiv: 'Миколайв',
  Simfer: 'Симферопль',
  Herson: 'Херсонская область'
}

const InfoBase = (props) => {
  return (
    <Box
      sx={{
        width: '100%',
        display: 'flex',
        flexDirection: 'row',
        justifyContent: 'space-evenly'
      }}
    >
      <List
        sx={{
          minWidth: '240px'
        }}
      >
        <ListItem>
          <ListItemText>
            {props.selected !== null ? (
              <Typography variant='h4' color='whitesmoke'>
                { city[props.selected.attributes.id.nodeValue] }
              </Typography>
            ) : (
              <Box>
                <Skeleton variant="text" sx={{ fontSize: '2rem' }} />
                <Skeleton variant="text" sx={{ marginTop: '0.05em', fontSize: '4rem' }} />
              </Box>
            )}
          </ListItemText>
        </ListItem>
      </List>

    </Box>
  )
}

export default InfoBase;
