import React, { useState, useEffect } from 'react';
import { ReactComponent as UkraineMap } from './ukraine.svg';
import './index.css';

const Map = (props) => {

  const [region, setRegion] = useState(null);
  const [listenerStatus, setListener] = useState(false);

  const handlerSelectRegion = (region) => {
    props.handler(region)
  }

  const setSelected = (region) => {
    setRegion(region);
  }

  const updateSelected = () => {
    if (region != null)
      region.firstChild.style.fill = 'black';
  }

  // eslint-disable-next-line
  useEffect(() => {

    if (props.selected != null) {
      console.log(listenerStatus)
      setSelected(props.selected);
      updateSelected(props.selected);
    }

    if (listenerStatus === false) {
      document.getElementById('map').childNodes.forEach(item => {
        item.addEventListener( 'click', function() {
          handlerSelectRegion(item);
          updateSelected();

          document.getElementById('map').childNodes.forEach(iterItem => {
            if (iterItem !== item)
              iterItem.firstChild.style.fill = '#202020'
          })

        })
        item.addEventListener( 'mouseover', function() {

          document.getElementById('map').childNodes.forEach(iterItem => {
            iterItem.firstChild.style.fill = '#202020'
            updateSelected();
          })

          item.firstChild.style.fill = '#ff0';
        })
        item.addEventListener( 'mouseout', function(){
          item.firstChild.style.fill = '#202020';

          document.getElementById('map').childNodes.forEach(iterItem => {
            iterItem.firstChild.style.fill = '#202020'
            updateSelected();
          })

        })
      });

      setListener(true);
    }

  })

  return (
    <div className="map">
      <UkraineMap />
    </div>
  )
}

export { Map };
