import React from 'react';
import Box from '@mui/material/Box';
import Skeleton from '@mui/material/Skeleton';
import Typography from '@mui/material/Typography';

import Card from './Card.js';

const style = {
  display: 'flex',
  flexDirection: 'column',
  justifyContent: 'space-evenly',
  width: '100%',
  height: '35vh',
  background: '#0009'
}

const city = {
  Ujgorod: 'Ужгород',
  Lviv: 'Львов',
  IvanFr: 'Ивано-франковск',
  Chernivci: 'Чернивци',
  Lutsk: 'Лутск',
  Ternop: 'Тернопиль',
  Rivne: 'Ривне',
  Hmel: 'Хмельницкий',
  Jitom: 'Житомыр',
  Vinn: 'Виница',
  Kiiv: 'Киев',
  Chernigiv: 'Чернигев',
  Sumy: 'Сумы',
  Cherkasy: 'Черкасы',
  Poltava: 'Полтава',
  Harkiv: 'Харькив',
  Lugansk: 'Луганск',
  Odesa: 'Одеса',
  Kirov: 'Киров',
  Dnepr: 'Днепр',
  Donetsk: 'Донецк',
  Zapor: 'Запорожье',
  Mykolaiv: 'Миколайв',
  Simfer: 'Симферопль',
  Herson: 'Херсонская область'
}

const Bar = (props) => {
  return (
    <div style={style}>
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'center'
        }}
      >
        {(props.selected === null ? (
          <Skeleton variant="rounded" width={300} height={60} />
        ) : (
          <Typography color="white" variant="h3" display="block">
            {city[props.selected.attributes.id.nodeValue]}
          </Typography>
        ))}
      </Box>
      <Box 
        sx={{
          display: 'flex',
          justifyContent: 'center',
          flexWrap: 'wrap',
          '& > :not(style)': {
            m: 1,
            width: 128,
            height: 128,
          }
        }}
      >
        {(props.data === undefined ? Array.from(new Array(7)) : props.data).map((item, key) => (
          <Box key={key} >
            {item !== undefined ? (
              <Card />
            ) : (
              <Skeleton variant="rectangular" width={128} height={128} />
            )}
          </Box>
        ))}
      </Box>
    </div>
  )
}

export default Bar
