import React from 'react';
import Box from '@mui/material/Box';
import { Typography, Skeleton } from '@mui/material';
import Chip from '@mui/material/Chip';
import Divider from '@mui/material/Divider';
import Stack from '@mui/material/Stack';
import DeviceThermostatIcon from '@mui/icons-material/DeviceThermostat';
import WindPowerIcon from '@mui/icons-material/WindPower';
import ThunderstormIcon from '@mui/icons-material/Thunderstorm';

import { City } from '../../constants';

const InfoBase = (props) => {
  console.log('13')
  return (
    <Box
      sx={{
        width: '100%',
        display: 'flex',
        flexDirection: 'row'
      }}
    >
      <Stack
        spacing={1}
        sx={{
          margin: 'auto',
          marginLeft: '2.5em',
          minWidth: '240px',
          justifyContent: 'start'
        }}
      >
        {props.selected !== null && Object.keys(props.data).length !== 0  ? (
          <Box
            sx={{
              display: 'flex',
              flexDirection: 'column',
              justifyContent: 'space-between',
              textAlign: 'center'
            }}
          >
            <Typography variant='h5' mt={-4} color='whitesmoke'>
              { City[props.selected.attributes.id.nodeValue] } region
            </Typography>
            <Typography variant='p' mt={2} color='whitesmoke'>
              {props.data.base.summary}  
            </Typography>
          </Box>
        ) : (
          <Box>
            <Skeleton variant="text" sx={{ fontSize: '3rem' }} />
            <Skeleton variant="text" sx={{ marginTop: '0em', fontSize: '5rem' }} />
          </Box>
        )}
      </Stack>
      <Box
        sx={{
          width: '100%',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center'
        }}
      >
        <Stack
          direction="row"
          divider={<Divider orientation="vertical" flexItem />}
          spacing={2}
          sx={{
            justifyContent: 'center'
          }}
        >
          {props.selected !== null && Object.keys(props.data).length !== 0  ? (
            Object.keys(props.data.cards).map((item, key) => (
              <Box key={key} sx={{ minWidth: '175px' }} >
                <Stack
                  sx={{
                    display: 'flex',
                    justifyContent: 'center'
                  }}
                >
                  <Chip label={`${item}`} sx={{ color: 'whitesmoke', marginTop: '1em' }} />
                  {
                    item === 'temperature'
                    ? <DeviceThermostatIcon color='action' sx={{ margin: 'auto', marginTop: '.25em', marginBottom: '.25em', fontSize: 60 }} />
                    : item === 'wind'
                    ? <WindPowerIcon color='action' sx={{ margin: 'auto', marginTop: '.25em', marginBottom: '.25em', fontSize: 60 }} />
                    : <ThunderstormIcon color='action' sx={{ margin: 'auto', marginTop: '.25em', marginBottom: '.25em', fontSize: 60 }} />
                  }
                  <Stack
                    direction="row"
                    sx={{ justifyContent: 'center' }}
                  >
                    {
                      typeof props.data.cards[item] === 'object'
                      ? Object.keys(props.data.cards[item]).map((subItem, subKey) => (
                        <Chip
                          key={subKey}
                          label={`${props.data.cards[item][subItem]}`}
                          sx={{ color: 'whitesmoke', margin: '0.25em' }}
                          variant="outlined" 
                        />
                      ))
                      : <Chip
                          label={`${props.data.cards[item]}°C`}
                          sx={{ color: 'whitesmoke' }}
                          variant="outlined" 
                        />
                    }
                  </Stack>
                </Stack>
              </Box>
            ))
          ) : Array.from(new Array(3)).map((value, key) => (
            <Skeleton key={key} variant="rounded" width={160} height={100} />
          ))}
        </Stack>
      </Box>
    </Box>
  )
}

export default InfoBase;
