import React, { useState, useEffect } from 'react';
import { ReactComponent as UkraineMap } from './ukraine.svg';
import './index.css';

const Map = (props) => {

  const [region, setRegion] = useState(null);

  const handlerSelectRegion = (region) => {
    updateSelected();
    props.handler(region)
  }

  const setSelected = (region) => {
    setRegion(region);
  }

  const updateSelected = () => {
    if (region != null)
      region.firstChild.style.fill = 'black';
  }

  useEffect(() => {
    const regions = document.getElementById('map').childNodes;

    if (props.selected != null) {
      setSelected(props.selected);
      updateSelected(props.selected);
    }

    for ( let i = 0; i < regions.length; i++ ) {
      regions[i].addEventListener( 'click', function() {
        handlerSelectRegion(regions[i]);
        setRegion(regions[i]);

        for ( let l = 0; l < regions.length; l++ ) {
          console.log(region)
          regions[l].firstChild.style.fill = '#202020'
        }

        updateSelected();
      })

      regions[i].addEventListener( 'mouseover', function() {

        for ( let l = 0; l < regions.length; l++ ) {
          regions[l].firstChild.style.fill = '#202020'
          updateSelected();
        }

        regions[i].firstChild.style.fill = '#ff0';
      })

      regions[i].addEventListener( 'mouseout', function(){
        regions[i].firstChild.style.fill = '#202020';

        for ( let l = 0; l < regions.length; l++ ) {
          regions[l].firstChild.style.fill = '#202020'
          updateSelected();
        }

      })
    }

  })

  return (
    <div className="map">
      <UkraineMap />
    </div>
  )
}

export { Map };
